mapper.select:
SELECT chat_no AS chatNo, chat_message AS chatMessage, send_time AS sendTime,
       store_id AS storeId, user_id AS userId, sender_type AS senderType, store_no AS storeNo
FROM chat
WHERE user_id = #{userId} AND store_id = #{storeId}
ORDER BY send_time DESC
    LIMIT #{limit}

SELECT * FROM chat
WHERE user_id = #{userId}
  AND store_id = #{storeId}
  AND send_time > #{lastCheckedTime}
