mapper.select:
SELECT user_id, user_pw, user_name, user_birth, user_phonenum, user_gender, user_signup
FROM USERS
WHERE user_id = #{userId}

SELECT user_id
FROM users
WHERE user_name = #{userName}
AND user_phonenum = #{phonenum}
AND user_pw != 'KAKAO' 
LIMIT 1

SELECT COUNT(*)
FROM USERS
WHERE user_id = #{userId}
  AND user_name = #{userName}
  AND user_phonenum = #{phonenum}
  AND user_pw != 'KAKAO'

mapper.update:
UPDATE USERS
SET user_pw = #{newPassword}
WHERE user_id = #{userId}
